package LetsCodeItUdemy;

public class ForLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 10; i++) {
			if (i == 5) {
				System.out.println("I value before break:" + " " + i);
				break;
			}
			System.out.println("Print i value" + " " + i);

		}

	}

}
